import React, { useState }from 'react';
import Sports from './pages/Sports';

function Proceeder() {
  var selectedValue = betSelector.value;
  console.log(selectedValue);
  if (selectedValue === 'Sports') {
    console.log('sports chosen');
    nbaScoresInfoBoard();
    displaySportsBetCard();
  } else if (selectedValue === 'Finance') {
    console.log('finance chosen');
    financeInfoBoard();
    //displayFinanceBetCard()
  }

  return (
      <div class="prediction-picker">
          <div class="card" id ="predictorCard">
              <select id ='betSelector' onclick='Proceeder(this.value)'>
                  <option value="">Select a Sector</option>
                  <option value="Sports">Sports</option>
                  <option value="Finance">Finance</option>
              </select>
          </div>
      </div>
  )
}

export default Proceeder;
